import { Button, Image, notification, Table, Tag } from "antd";
import Column from "antd/lib/table/Column";
import React from "react";
import { formatMoney } from "../../utils/formatNumber";

const TableProduct = ({ listProduct, typeArray, showModal, deleteProduct }) => {
    return (
        <Table
            size={"small"}
            dataSource={listProduct}
            rowKey="_id"
            pagination={{ defaultPageSize: 3, position: ["bottomRight"] }}
        >
            <Column
                title="Tên món"
                dataIndex="name"
                key="name"
                sorter={(a, b) => a.name.length - b.name.length}
            />
            <Column
                title="Hình ảnh"
                dataIndex="image"
                key="image"
                render={(image) =>
                    image ? (
                        <Image width={70} src={`http://${image}`} />
                    ) : (
                        <Image
                            width={70}
                            src="error"
                            fallback="data:image/png;base64,"
                        />
                    )
                }
            />
            <Column
                title="Loại"
                dataIndex="type"
                key="type"
                filters={typeArray}
                onFilter={(value, product) => product.type._id === value}
                render={(type) => <span>{type.name}</span>}
                filtered={(value) => console.log(value)}
            />
            <Column
                title="Đơn giá"
                dataIndex="price"
                key="price"
                render={(price) => <span>{`${formatMoney(price)} VNĐ`}</span>}
                sorter={(a, b) => a.price - b.price}
            />
            <Column
                title="Hàng hóa"
                dataIndex="comoditys"
                key="_id"
                render={(comoditys) =>
                    comoditys.map((item) => (
                        <Tag key={item._id} color="blue">
                            {item.name}
                        </Tag>
                    ))
                }
            />
            <Column
                colSpan="2"
                title="Action"
                dataIndex="_id"
                key="_id"
                render={(_id) => (
                    <div className="button_group">
                        <Button
                            type="primary"
                            danger
                            onClick={() =>
                                notification["error"]({
                                    key: `${_id}`,
                                    message: "Bạn có đồng ý xoá món ăn này",
                                    btn: (
                                        <Button
                                            type="primary"
                                            size="small"
                                            onClick={() => {
                                                deleteProduct(_id);
                                                notification.close(_id);
                                            }}
                                        >
                                            Xác nhận
                                        </Button>
                                    ),
                                })
                            }
                        >
                            Xoá
                        </Button>
                        <Button type="primary" onClick={() => showModal("Cập nhật", _id)}>
                            Cập nhật
                        </Button>
                    </div>
                )}
            />
        </Table>
    );
};

export default TableProduct;
